{% set terms_exist = doc.terms and (doc.terms|striptags).strip() or "" %}
{% if letter_head and not no_letterhead -%}
    <div class="letter-head">{{ letter_head }}</div>
    <hr>
{%- endif %}
<div {% if terms_exist -%} class="page-break" {%- endif %}>

<div style="font-family: Arial, sans-serif; max-width: 800px; margin: 0 auto; padding: 10px;">
    <!-- Letter Header -->
    <div style="margin-bottom: 30px;">
        <p><strong>Date:</strong> {{ frappe.utils.formatdate(doc.offer_date, "dd/MM/yyyy") }}</p>

        <p><strong>To,</strong><br>
        {{ doc.applicant_name }}<br>
        {% if doc.applicant_email %}{{ doc.applicant_email }}<br>{% endif %}
        {% if doc.custom_applicant_address %}{{ doc.custom_applicant_address }}<br>{% endif %}
        </p>

        <p style="margin: 20px 0;"><strong>Subject: Offer of Internship</strong></p>
    </div>

    <!-- Letter Body -->
    <div style="line-height: 1.6; margin-bottom: 30px;">
        <p>Dear {{ doc.applicant_name }},</p>

        <p>We are pleased to appoint you in the services of {{ doc.company }} on the terms and conditions detailed in this letter.</p>

        <p>Your designation shall be <strong>{{ doc.designation }}</strong>.</p>

        <h3 style="color: #003366; margin-top: 20px; margin-bottom: 15px;"><strong>Internship Terms and Conditions:</strong></h3>

        <!-- job offer terms and description from the table with numbering -->
        {%- if doc.offer_terms -%}
            {%- for row in doc.offer_terms -%}
                <p><strong>{{ loop.index }}. {{ row.offer_term }}:</strong></p>
                <div style="margin-left: 20px;">
                    {%- set value_lines = row.value.split('•') if '•' in row.value else row.value.split('\n') -%}
                    {%- if value_lines|length > 1 -%}
                        {%- for line in value_lines -%}
                            {%- if line.strip() -%}
                                <p style="margin: 5px 0;">• {{ line.strip() }}</p>
                            {%- endif -%}
                        {%- endfor -%}
                    {%- else -%}
                        {%- set value_sentences = row.value.split('.') -%}
                        {%- if value_sentences|length > 2 -%}
                            {%- for sentence in value_sentences -%}
                                {%- if sentence.strip() -%}
                                    <p style="margin: 5px 0;">• {{ sentence.strip() }}{% if not sentence.strip().endswith('.') %}.{% endif %}</p>
                                {%- endif -%}
                            {%- endfor -%}
                        {%- else -%}
                            <p style="margin: 5px 0;">{{ row.value }}</p>
                        {%- endif -%}
                    {%- endif -%}
                </div>
                <br>
            {%- endfor -%}
        {%- endif -%}

        <p>Please read the detailed terms as below. If you have any queries, feel free to get in touch with us.</p>

        <p>Please confirm your acceptance by signing and returning this letter. We look forward to having you as part of our team.</p>
    </div>

    <!-- Signature Section -->
    <div style="margin-top: 40px;">
        <p><strong>Yours sincerely,</strong></p>
        <br><br><br>
        <p>_______________________<br>
        Authorized Signatory<br>
        {{ doc.company }}</p>
        <img src="/files/GennextSeal.png" alt="Gennext Company Seal" style="height: 60px; margin-top: 10px;">
    </div>

    <!-- Acceptance Section -->
    <div style="margin-top: 50px; padding-top: 20px;">
        <p><strong>Acceptance:</strong> I, {{ doc.applicant_name }}, accept the above terms and conditions of internship.</p>
        <div style="margin-top: 30px;">
            <p>Signature: _________________ &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Date: _________</p>
        </div>
    </div>
</div>

